import org.hyperledger.fabric.shim.ledger.KeyValue;
import org.hyperledger.fabric.shim.ledger.QueryResultsIterator;

import java.util.ArrayList;
import java.util.Iterator;
import java.util.List;

public class SQLOJContractTest {
    private final class MockKeyValue implements KeyValue {

        private final String key;
        private final String value;

        MockKeyValue(final String key, final String value) {
            super();
            this.key = key;
            this.value = value;
        }

        @Override
        public String getKey() {
            return this.key;
        }

        @Override
        public String getStringValue() {
            return this.value;
        }

        @Override
        public byte[] getValue() {
            return this.value.getBytes();
        }

    }

    private final class MockAssetResultsIterator implements QueryResultsIterator<KeyValue> {

        private final List<KeyValue> recordList;

        MockAssetResultsIterator() {
            super();

            recordList = new ArrayList<KeyValue>();
            recordList.add(new MockKeyValue("Record_1", "{ \"ID\": \"1\", \"studentNumber\": \"\", \"recordType\": \"1\", \"score\": \"\"}"));
            recordList.add(new MockKeyValue("Record_2", "{ \"ID\": \"2\", \"studentNumber\": \"\", \"recordType\": \"2\", \"score\": \"\"}"));
            recordList.add(new MockKeyValue("Record_3", "{ \"ID\": \"3\", \"studentNumber\": \"\", \"recordType\": \"1\", \"score\": \"\"}"));
            recordList.add(new MockKeyValue("Record_4", "{ \"ID\": \"4\", \"studentNumber\": \"\", \"recordType\": \"2\", \"score\": \"\"}"));
            recordList.add(new MockKeyValue("Record_5", "{ \"ID\": \"5\", \"studentNumber\": \"\", \"recordType\": \"1\", \"score\": \"\"}"));
            recordList.add(new MockKeyValue("Record_6", "{ \"ID\": \"6\", \"studentNumber\": \"\", \"recordType\": \"2\", \"score\": \"\"}"));

        }

        @Override
        public Iterator<KeyValue> iterator() {
            return recordList.iterator();
        }

        @Override
        public void close() throws Exception {
            // do nothing
        }

    }
}
